from django.db import modelsclass Genre(models.Model):    name = models.CharField(max_length=100)    def __str__(self):        return self.nameclass Collection(models.Model):    name = models.CharField(max_length=200)    poster_path = models.CharField(max_length=200, blank=True, null=True)    backdrop_path = models.CharField(max_length=200, blank=True, null=True)    def __str__(self):        return self.nameclass Movie(models.Model):    title = models.CharField(max_length=100)    overview = models.TextField()    release_date = models.DateField(null=True, blank=True)    genres = models.ManyToManyField(Genre)    runtime = models.PositiveIntegerField()    revenue = models.DecimalField(max_digits=15, decimal_places=2)    budget = models.DecimalField(max_digits=15, decimal_places=2)    poster_url = models.URLField(max_length=200, blank=True, null=True)    tagline = models.CharField(max_length=255, blank=True, null=True)    belongs_to_collection = models.ForeignKey(Collection, null=True, blank=True, on_delete=models.SET_NULL)    def __str__(self):        return self.title